import { useState } from 'react';
import './App.css';

// const fruits = ["Apple", "Mango", "Banana", "Grapes"];

const fruits = [{
  name: "Apple",
  pic: "data:image/jpeg;base64,"
},
{
  name: "Mango",
  pic: "data:image/jpeg;base64,"
},
{
  name: "Banana",
  pic: "data:image/jpeg;base64,"
}];

function App() {
  return (
    <div className="App">
      {fruits.map(val => {
        return (
          <>
            <FruitsName name={val.name} pic={val.pic} />
            <Quantity />
          </>
        );
      })}
    </div>
  );
}

function FruitsName({ name, pic }) {
  return (
    <div>
      <img src={pic} alt={name} />
      <p>{name}</p>
    </div>
  );
}

function Quantity() {
  const [quantity, setQuantity] = useState(0);
  const styles = {
    color: quantity > 3 ? "green" : "blue"
  }
  return (
    <div>
      {quantity >= 3 ? <p style={styles}>congratulations got 1 kg free</p> : null}
      {quantity >= 3 ? null : <p>Buy 3 kg and get 1 kg free</p>}
      <button onClick={() => setQuantity(quantity + 1)}> + </button>
      <span> {quantity} kg </span>
      <button onClick={() => setQuantity(quantity - 1)}> - </button>
    </div>
  );
}

export default App;
